import React from "react";
import "../styles/A.css";
import pic from "../picture/grill.jpeg";

function Aboutme() {
  return ( 

    <>
    
    <div>
        <img src={pic} alt=""/>
      </div>
      <div class="flex bg-gray-100 p-10">
        <div class="mb-auto mt-auto max-w-lg">
          <h1 class="text-3xl uppercase">William Pollak</h1>
          <p class="font-semibold mb-5">Web Developer</p>
      
          <p> I currently live in thornton colorado, but am originally from michigan. I enjoy playing sports and video games in my free time. Web Developer trained at the University of Denver Coding Bootcamp and earned a full Stack Web Development Certificate. Innovative problem-solver passionate about developing apps, focused on mobile-first design and development. Strengths in creativity, teamwork, and building projects from ideation to execution. To secure a position that will utilize my skills and experience to maximum potential, with the opportunity for advancement based on performance. 
          </p>
          <button class="bg-black rounded-md py-3 px-7 mt-6 text-white"><a href="mailto:soccerreferee7@gmail.com">Email Me</a></button>
        </div>
  </div>
      </>   
      
      
    );
    }

export default Aboutme;
